import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: {
        operator: ""
      },
      operator: [],
      viewbyfaction: false,
      viewbyrelease: false,
      viewall: false
    };
  }
  componentDidMount() {
    this.refreshList();
  }
  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };
  viewItem = item => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  viewFaction = () => {
    this.setState({ viewbyfaction: !this.state.viewbyfaction });
  };
  refreshList = () => {
    axios
      .get("http://localhost:9001/api/operators/")
      .then(res => this.setState({ operator: res.data }))
      .catch(err => console.log(err));
  };
  renderOps = (s,e) => {
    const newItems = this.state.operator;
    return newItems.slice(s,e).map(item => (
      <>
        <span
          operator={item.operator} onClick={() => this.viewItem(item)}>
            <img src={require(`./Images/${item.operator}_Badge.png`)} alt="badge"></img>
            {item.operator}
            <img src={require(`./Images/${item.operator}.png`)} alt="op"></img>
        </span>
      </>
    ));
  };
  render() {
    let factions = []
    if ( this.state.viewbyfaction ) {
      return (
      <main className="content">
        <button className="btn" onClick={this.viewFaction}>Go Back</button>
        <ul className="list-group list-group-flush">
          FBI
          {this.renderOps(0,4)}
        </ul>
        <ul className="list-group list-group-flush">
          GIGN
          {this.renderOps(4,8)}
        </ul>
        <ul className="list-group list-group-flush">
          SAS
          {this.renderOps(8,12)}
        </ul>
        <ul className="list-group list-group-flush">
        Spetsnaz
          {this.renderOps(12,16)}
        </ul>
        <ul className="list-group list-group-flush">
        GSG 9
          {this.renderOps(16,20)}
        </ul>
        <ul className="list-group list-group-flush">
        JTF-2
          {this.renderOps(20,22)}
        </ul>
      </main>
      )}
    return (
      <main className="content">
        <h1 className="text text-uppercase text-center my-4">R6 Counter</h1>
          <button className="btn" onClick={this.viewFaction}>View by Faction</button>
          {factions}
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
          />
        ) : null}
      </main>
    );
    }
  }
export default App;
