import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: {
        operator: ""
      },
      operator: []
    };
  }
  componentDidMount() {
    this.refreshList();
  }
  refreshList = () => {
    axios
      .get("http://localhost:9001/api/operators/")
      .then(res => this.setState({ operator: res.data }))
      .catch(err => console.log(err));
  };
  renderItems = () => {
    const newItems = this.state.operator;
    return newItems.map(item => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          operator={item.operator}>
            {item.operator}
        </span>
        <span>
              <button
                onClick={() => this.viewItem(item)}
                className="btn btn-secondary mr-2"
              >
                View{" "}
              </button>
            </span>
      </li>
    ));
  };
  viewItem = item => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  render() {
    return (
      <main className="content">
        <h1 className="text text-uppercase text-center my-4">R6 Counter</h1>
        <div className="row ">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
            <div className="card p-3">
              <div className="">
              </div>
              <ul className="list-group list-group-flush">
                {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
            onSave={this.handleSubmit}
          />
        ) : null}
      </main>
    );
  }
}
export default App;
