// frontend/src/components/Modal.js

import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      siege: this.props.operator
    };
  }

  render() {
    const { toggle, onSave } = this.props;
    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}> Operator </ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="operator">Operator</Label>
              <Input
                type="text"
                name="operator"
                value={this.state.operator.operator}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="faction">Faction</Label>
              <Input
                type="text"
                name="faction"
                value={this.state.operator.faction}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="gadget">Gadget</Label>
              <Input
                type="text"
                name="gadget"
                value={this.state.operator.gadget}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="equipment">Equipment</Label>
              <Input
                type="text"
                name="equipment"
                value={this.state.operator.equipment}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="armor">Armor</Label>
              <Input
                type="text"
                name="armor"
                value={this.state.operator.armor}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="speed">Speed</Label>
              <Input
                type="text"
                name="speed"
                value={this.state.operator.speed}
                onChange={this.handleChange}
              />
            </FormGroup>
            <FormGroup>
              <Label for="counters">Counters</Label>
              <Input
                type="text"
                name="counters"
                value={this.state.operator.counters}
                onChange={this.handleChange}
              />
            </FormGroup>
          </Form>
        </ModalBody>
      </Modal>
    );
  }
}
