import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: {
        operator: ""
      },
      operators: []
    };
  }
  componentDidMount() {
    this.refreshList();
  }
  refreshList = () => {
    axios
      .get("http://localhost:9001/api/siege/")
      .then(res => this.setState({ siege: res.data }))
      .catch(err => console.log(err));
  };
  renderItems = () => {
    const { viewCompleted } = this.state;
