import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: this.props.activeItem
    };
  }
  handleClose = e => {this.hideModal}
  render() {
    const { toggle } = this.props;
    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}> Operator </ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label>{this.state.activeItem.operator}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.faction}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.gadget}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.equipment}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.armor}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.speed}</Label>
            </FormGroup>
            <FormGroup>
              <Label>{this.state.activeItem.counters}</Label>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="success" onClick={() => toggle}>
            X
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
